        setEntry([...entries, {id:uuid}])
    }


    return (
        <div>

            <h2>Your Library</h2>
            <p>Here is your library of what you are reading or have read in the past.</p>
            <EntriesForm addEntry = {addEntry} />
        </div>
    );
}

export default Library;
