import React, {useState} from 'react';
import {EntriesForm} from '../components/EntriesForm'
import {v4 as uuidv4} from 'uuid';

function Library() {
    const [entries, setEntries] = useState([])

    const addEntry = entry => {
        setEntry([...entries, {id:  uuidv4(), add: entry, completed: false, isEditing: false }])
    }


    return (
        <div>

            <h2>Your Library</h2>
            <p>Here is your library of what you are reading or have read in the past.</p>
            <EntriesForm addEntry = {addEntry} />
        </div>
    );
}

export default Library;
