import React, {useState} from 'react'

export const EntriesForm = ({addEntry}) => {
    const [value, setValue] = useState("")

    const handleAdd = e => {
        e.preventDefault();

        addEntry(value)

    }
    return(
        <form className='entries-form' onAdd={handleAdd}>
            <input type="text" className='entry-input' placeholder='I have read..' onChange={(e) => setValue(e.target.value)}/>
            <button type='add' className='add-entry-button'>Add Book</button>
        </form>
    )
}
